import React, { useState } from 'react';
import Chart from 'chart.js/auto';
import {
    Container, Header, HeaderContainer,
    Tagline, MapContainer, 
    PromptContainer, TextArea, Button, GenerateButton,
  GlobalStyle,MapIframe,
    LoadingMessage,

} from './styl.js';

function App() {
  const [prompt, setPrompt] = useState('');
    const [loading, setLoading] = useState(false);
    const [mapHTML, setMapHTML] = useState("http://127.0.0.1:5000/maps/filtered_map.html");
    const [selectedId, setSelectedId] = useState("");
    const [panoImage, setPanoImage] = useState(null); // State to store the panorama image

    const handleConfirmSelection = async () => {
        if (!selectedId) {
            alert("Please enter an Asset ID");
            return;
        }

        setPanoLoading(true); // Start loading before fetch

        try {
            const response = await fetch("http://127.0.0.1:5000/register-point", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ id: selectedId }),
            });

            const data = await response.json();



        } catch (error) { // âœ… Check for syntax issues here
            console.error("Error fetching panorama:", error);
        } finally {
            setPanoLoading(false);
        }
    };

    const [filters, setFilters] = useState({
      metroStation: false,
      restaurants: false,
      bars: false,
      parks: true

  });

  const [mapLoading, setMapLoading] = useState(false);
  const [panoLoading, setPanoLoading] = useState(false);

  const handlePromptSubmit = async () => {
    setMapLoading(true);


    if (barGraph1Title) {
        barGraph1Title.innerText = "Loading...";
    } else {
        console.warn("Warning: barGraph1Title not found.");
    }

    try {
        const response = await fetch("http://127.0.0.1:5000/process-prompt", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ prompt }),
        });

        const data = await response.json();

        if (data.error) {
            console.error("ðŸš¨ Server Error:", data.error);
            if (barGraph1Title) barGraph1Title.innerText = "Error: No Data";
            if (barGraph2Title) barGraph2Title.innerText = "Error: No Data";
            setMapLoading(false);
            return;
        }

        // âœ… Force map to update immediately
        const newMapUrl = `http://127.0.0.1:5000/maps/filtered_map.html?t=${new Date().getTime()}`;

        setMapHTML(""); // Directly update the map URL

        setTimeout(() => {
            setMapHTML(newMapUrl);
        }, 50);

        setTimeout(() => {
            setMapLoading(false);
        }, 500);

        // âœ… Update Bar Graphs
        if (!data.barGraphData || !data.barGraphData.price_range || !data.barGraphData.constructed_area) {
            console.error("ðŸš¨ No bar graph data returned from the backend.");
            if (barGraph1Title) barGraph1Title.innerText = "Error: No Data";
            if (barGraph2Title) barGraph2Title.innerText = "Error: No Data";
            setMapLoading(false);
            return;
        }
        updateBarGraph("barGraph1", "Price Range vs. Apartments", data.barGraphData.price_range);
        updateBarGraph("barGraph2", "Constructed Area vs. Apartments", data.barGraphData.constructed_area);

        // âœ… Reset Titles after Loading
        if (barGraph1Title) barGraph1Title.innerText = "Price Range vs. Apartments";
        if (barGraph2Title) barGraph2Title.innerText = "Constructed Area vs. Apartments";

        setMapLoading(false);

    } catch (error) {
        console.error("Error fetching filtered map:", error);
        setMapLoading(false);
    }
};


function updateBarGraph(elementId, title, data) {
    console.log(`ðŸ“ˆ Updating Chart for ${elementId}`, data); // âœ… Debugging

    const canvas = document.getElementById(elementId);
    if (!canvas) {
        console.error('Canvas element ${elementId} not found!');
        return;
    }

    // âœ… Ensure the canvas width and height fit the parent container
    canvas.style.width = "600px";
    canvas.style.maxwidth = "100%";
    canvas.style.height = "100%";

    const ctx = canvas.getContext("2d");

    // âœ… Check if a chart instance exists before destroying it
    if (window[elementId] && typeof window[elementId].destroy === "function") {
        window[elementId].destroy();
    }

    // âœ… Create a new interactive bar chart
    window[elementId] = new Chart(ctx, {
        type: "bar",
        data: {
            labels: data.labels,
            datasets: [{
                label: title,
                data: data.counts,
                backgroundColor: "rgba(62, 92, 170, 0.7)",
                borderColor: "rgba(62, 92, 170, 1)",
                borderWidth: 2
            }]
        },
        options: {
            responsive: true,
            maintainAspectRatio: false,
            layout: {
                padding: { top: 10, left: 10, right: 10, bottom: 10 }
            },
            plugins: {
                legend: {display: false},
                tooltip: {
                    enabled: true
                }
            },
            scales: {
                x: {display: true},
                y: { beginAtZero: true }
            }
        }
    });

    console.log(`âœ… Chart updated for ${elementId}`);
}

    // Mock data for demonstration
    const buildingInfo = {
        price: "â‚¬325,000",
        bedrooms: "2",
        bathrooms: "1",
        area: "78mÂ²",
        floor: "3rd"
    };

    const handleFilterChange = (filterName) => {
        setFilters(prevFilters => ({
            ...prevFilters,
            [filterName]: !prevFilters[filterName]
        }));
    };

    const handleGenerate = () => {
        console.log("Generating results with prompt:", prompt);
        console.log("Active filters:", filters);
        // Here you would call your API or data processing logic
    };

    return (
        <>
            <GlobalStyle />
            <Container>
                <HeaderContainer>
                    <HeaderLogo>
                        <svg width="30" height="30" viewBox="0 0 24 24">
                            <path d="M3,13h8V3H3V13z M3,21h8v-6H3V21z M13,21h8V11h-8V21z M13,3v6h8V3H13z" fill="#3e5caa"/>
                        </svg>
                        <Header>Insite BCN</Header>
                    </HeaderLogo>
                    <HeaderNav>
                        <Button>Home</Button>
                        <Button>Menu</Button>
                    </HeaderNav>
                </HeaderContainer>

                <MapContainer>
                    {mapLoading ? (
                        <LoadingMessage>Loading...</LoadingMessage>
                    ) : (
                        <iframe
                            key={mapHTML}
                            src={mapHTML}
                            title="Interactive Map"
                            style={{
                                width: "100%",
                                height: "100vh",
                                border: "none",
                                display: "block"
                            }}
                            onLoad={() => setMapLoading(false)} // ðŸ”¥ Hide loading text once the map loads
                        />
                    )}
                </MapContainer>

                <StreetView>
                    {panoLoading ? (
                        <p>Loading...</p>
                    ) : panoImage ? (
                        <img
                            src={panoImage}
                            alt="Panoramic View"
                            style={{ width: "100%", height: "100%", objectFit: "cover" }}
                            onLoad={() => setPanoLoading(false)}
                        />
                    ) : (
                        <p>PANORAMIC STREET VIEW</p>
                    )}
                </StreetView>

                <PromptContainer>
                    <TextArea 
                        placeholder="Enter prompt here. For eg houses under â‚¬50k with balconies and lift access" 
                        value={prompt}
                        onChange={(e) => setPrompt(e.target.value)}
                    />

                    <GenerateButton onClick={handlePromptSubmit}> {/*handlegenrate*/}
                        Generate
                    </GenerateButton>

                    <FilterContainer>
                        <div>
                            <FilterCheckbox 
                                type="checkbox" 
                                id="metroStation" 
                                checked={filters.metroStation}
                                onChange={() => handleFilterChange('metroStation')}
                            />
                            <FilterLabel htmlFor="metroStation">Metro station</FilterLabel>
                        </div>

                        <div>
                            <FilterCheckbox 
                                type="checkbox" 
                                id="restaurants" 
                                checked={filters.restaurants}
                                onChange={() => handleFilterChange('restaurants')}
                            />
                            <FilterLabel htmlFor="restaurants">Restaurants</FilterLabel>
                        </div>

                        <div>
                            <FilterCheckbox 
                                type="checkbox" 
                                id="bars" 
                                checked={filters.bars}
                                onChange={() => handleFilterChange('bars')}
                            />
                            <FilterLabel htmlFor="bars">Bars</FilterLabel>
                        </div>

                        <div>
                            <FilterCheckbox 
                                type="checkbox" 
                                id="parks" 
                                checked={filters.parks}
                                onChange={() => handleFilterChange('parks')}
                            />
                            <FilterLabel htmlFor="parks">Parks</FilterLabel>
                        </div>
                    </FilterContainer>

                    <Button className="amenity-score-button">
                        Amenity Score
                    </Button>

                    <GenerateButton onClick={handleGenerate} style={{width: '150px', marginLeft: 'auto'}}>
                        Generate
                    </GenerateButton>
                </PromptContainer>

                <AssetIDcontainer>
                    <AssetIDTextArea 
                        placeholder="Enter Asset ID here" 
                        value={selectedId}
                        onChange={(e) => setSelectedId(e.target.value)}
                    />

                    <AssetIDConfirmButton onClick={handleConfirmSelection}> {/*handlegenrate*/}
                        Confirm Selection
                    </AssetIDConfirmButton>
                </AssetIDcontainer>


                <AnalyticsSection>
                    <AnalyticsTitle>Analytics</AnalyticsTitle>

                    <h3>Price Range vs. Apartments</h3>
                    <BarGraphContainer>
                        <canvas id="barGraph1"></canvas>
                    </BarGraphContainer>

                    <h3>Constructed Area vs. Apartments</h3>
                    <BarGraphContainer>
                        <canvas id="barGraph2"></canvas>
                    </BarGraphContainer>
                </AnalyticsSection>

                <BuildingInfoSection>
                    <BuildingInfoTitle>Building info</BuildingInfoTitle>

                    <PropertyDetail>
                        <PropertyIcon>
                            <svg width="24" height="24" viewBox="0 0 24 24">
                                <path d="M12,3L2,12h3v8h6v-6h2v6h6v-8h3L12,3z" fill="#3e5caa"/>
                            </svg>
                        </PropertyIcon>
                        <PropertyValue>
                            <div>Price</div>
                            <div className="output">{buildingInfo.price}</div>
                        </PropertyValue>
                    </PropertyDetail>

                    <PropertyDetail>
                        <PropertyIcon>
                            <svg width="24" height="24" viewBox="0 0 24 24">
                                <path d="M7,13c1.66,0,3-1.34,3-3S8.66,7,7,7s-3,1.34-3,3S5.34,13,7,13z M19,7h-8v7H3V7H1v10h22V11C23,8.79,21.21,7,19,7z" fill="#3e5caa"/>
                            </svg>
                        </PropertyIcon>
                        <PropertyValue>
                            <div>Bedrooms</div>
                            <div className="output">{buildingInfo.bedrooms}</div>
                        </PropertyValue>
                    </PropertyDetail>

                    <PropertyDetail>
                        <PropertyIcon>
                            <svg width="24" height="24" viewBox="0 0 24 24">
                                <path d="M7,19H5V5h2V19z M19,5H9v2h10V5z M9,19h10v-2H9V19z" fill="#3e5caa"/>
                            </svg>
                        </PropertyIcon>
                        <PropertyValue>
                            <div>Bathrooms</div>
                            <div className="output">{buildingInfo.bathrooms}</div>
                        </PropertyValue>
                    </PropertyDetail>

                    <PropertyDetail>
                        <PropertyIcon>
                            <svg width="24" height="24" viewBox="0 0 24 24">
                                <path d="M3,5v14h18V5H3z M7,7v2H5V7H7z M5,13v-2h2v2H5z M5,15h2v2H5V15z M19,17H9v-2h10V17z M19,13H9v-2h10V13z M19,9H9V7h10V9z" fill="#3e5caa"/>
                            </svg>
                        </PropertyIcon>
                        <PropertyValue>
                            <div>Area(sqm)</div>
                            <div className="output">{buildingInfo.area}</div>
                        </PropertyValue>
                    </PropertyDetail>

                    <PropertyDetail>
                        <PropertyIcon>
                            <svg width="24" height="24" viewBox="0 0 24 24">
                                <path d="M4,14h4v-4H4V14z M4,19h4v-4H4V19z M4,9h4V5H4V9z M9,14h12v-4H9V14z M9,19h12v-4H9V19z M9,5v4h12V5H9z" fill="#3e5caa"/>
                            </svg>
                        </PropertyIcon>
                        <PropertyValue>
                            <div>Floor</div>
                            <div className="output">{buildingInfo.floor}</div>
                        </PropertyValue>
                    </PropertyDetail>
                </BuildingInfoSection>
            </Container>
        </>
    );
}

export default App;
