import React, { useState, useEffect } from 'react';
import { MapContainer, LoadingMessage } from '../style_Desire';

const MapView = ({ mapUrl, onMapLoad, onMapError }) => {
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        setIsLoading(true);
        setError(null);
    }, [mapUrl]);

    const handleLoad = () => {
        setIsLoading(false);
        if (onMapLoad) onMapLoad();
    };

    const handleError = (e) => {
        setIsLoading(false);
        setError('Failed to load map');
        if (onMapError) onMapError(e);
    };

    return (
        <MapContainer>
            {isLoading && <LoadingMessage>Loading map...</LoadingMessage>}
            {error && <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>}
            <iframe
                key={mapUrl}
                src={mapUrl}
                title="Interactive Map"
                style={{
                    width: "100%",
                    height: "100vh",
                    border: "none",
                    display: "block"
                }}
                onLoad={handleLoad}
                onError={handleError}
            />
        </MapContainer>
    );
};

export default MapView;
