import React, { useState, useEffect } from 'react';
import {
    Container,
    GlobalStyle,
    MapContainer,
    PromptContainer,
    TextArea,
    GenerateButton,
    LoadingMessage
} from './style.js';

function App() {
    const [prompt, setPrompt] = useState('');
    const [loading, setLoading] = useState(false);
    const [mapHTML, setMapHTML] = useState("http://127.0.0.1:5000/maps/filtered_map.html");

    useEffect(() => {
        const handleResize = () => {
            const iframe = document.querySelector('iframe');
            if (iframe) {
                iframe.contentWindow.postMessage('resize', '*');
            }
        };

        // Initial resize
        handleResize();

        // Add resize listener
        window.addEventListener('resize', handleResize);

        // Cleanup
        return () => window.removeEventListener('resize', handleResize);
    }, [mapHTML]);

    const handlePromptSubmit = async () => {
        if (!prompt.trim()) return;

        setLoading(true);

        try {
            const response = await fetch("http://127.0.0.1:5000/process-prompt", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ prompt }),
            });

            const data = await response.json();
            if (data.error) {
                console.error("Server Error:", data.error);
                return;
            }

            const newMapUrl = `http://127.0.0.1:5000/maps/filtered_map.html?t=${new Date().getTime()}`;
            setMapHTML("");
            setTimeout(() => {
                setMapHTML(newMapUrl);
            }, 50);

        } catch (error) {
            console.error("Error:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <>
            <GlobalStyle />
            <Container style={{ overflow: 'hidden', position: 'fixed', width: '100vw', height: '100vh' }}>
                <MapContainer style={{ width: '100vw', height: '100vh', position: 'relative' }}>
                    {loading ? (
                        <LoadingMessage>Loading...</LoadingMessage>
                    ) : (
                        <iframe
                            key={mapHTML}
                            src={mapHTML}
                            title="Interactive Map"
                            style={{
                                width: '100%',
                                height: '100%',
                                border: 'none',
                                position: 'absolute',
                                top: 0,
                                left: 0
                            }}
                            onLoad={(e) => {
                                e.target.contentWindow.postMessage('resize', '*');
                            }}
                        />
                    )}
                </MapContainer>

                <PromptContainer
                    style={{
                        position: "fixed",
                        bottom: "2rem",
                        left: "50%",
                        transform: "translateX(-50%)",
                        display: "flex",
                        gap: "1rem",
                        padding: "1rem",
                        background: "rgba(255, 255, 255, 0.9)",
                        borderRadius: "8px",
                        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                        zIndex: 1000
                    }}
                >
                    <TextArea 
                        placeholder="Enter your search query..." 
                        value={prompt}
                        onChange={(e) => setPrompt(e.target.value)}
                        onKeyPress={(e) => {
                            if (e.key === 'Enter' && !e.shiftKey) {
                                e.preventDefault();
                                handlePromptSubmit();
                            }
                        }}
                        style={{
                            width: "400px",
                            padding: "0.5rem",
                            borderRadius: "4px",
                            border: "1px solid #ccc"
                        }}
                    />
                    <GenerateButton 
                        onClick={handlePromptSubmit}
                        style={{
                            padding: "0.5rem 1rem",
                            borderRadius: "4px",
                            background: "#3e5caa",
                            color: "white",
                            border: "none",
                            cursor: "pointer"
                        }}
                    >
                        Search
                    </GenerateButton>
                </PromptContainer>
            </Container>
        </>
    );
}

export default App;
