import React, { useState } from 'react';
import {
    Container, Header, HeaderContainer, HeaderLogo, HeaderNav,
    Tagline, PromptContainer, TextArea, Button, GenerateButton,
    FilterContainer, FilterCheckbox, FilterLabel, GlobalStyle,
    AssetIDcontainer, AssetIDTextArea, AssetIDConfirmButton
} from './style_Desire';
import MapView from './components/MapView';
import Analytics from './components/Analytics';
import StreetView from './components/StreetView';

function App() {
    const [prompt, setPrompt] = useState('');
    const [loading, setLoading] = useState(false);
    const [mapUrl, setMapUrl] = useState("http://127.0.0.1:5000/maps/filtered_map.html");
    const [statistics, setStatistics] = useState(null);
    const [selectedCoordinates, setSelectedCoordinates] = useState(null);
    const [selectedId, setSelectedId] = useState("");
    const [filters, setFilters] = useState({
        metroStation: false,
        restaurants: false,
        bars: false,
        parks: true
    });

    const handlePromptSubmit = async () => {
        if (!prompt.trim()) {
            alert("Please enter a query");
            return;
        }

        setLoading(true);
        setStatistics(null);

        try {
            const response = await fetch("http://127.0.0.1:5000/process-prompt", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ prompt }),
            });

            const data = await response.json();

            if (data.error) {
                console.error("Error:", data.error);
                setLoading(false);
                return;
            }

            // Update map URL with timestamp to force refresh
            const newMapUrl = `http://127.0.0.1:5000/maps/filtered_map.html?t=${new Date().getTime()}`;
            setMapUrl(newMapUrl);

            // Update statistics if available
            if (data.statistics) {
                setStatistics(data.statistics);
            }

        } catch (error) {
            console.error("Error:", error);
        } finally {
            setLoading(false);
        }
    };

    const handleConfirmSelection = async () => {
        if (!selectedId) {
            alert("Please enter an Asset ID");
            return;
        }

        setLoading(true);

        try {
            const response = await fetch("http://127.0.0.1:5000/register-point", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ id: selectedId }),
            });

            const data = await response.json();
            if (data.error) {
                throw new Error(data.error);
            }

            // Update coordinates if available
            if (data.coordinates) {
                setSelectedCoordinates(data.coordinates);
            }
        } catch (error) {
            console.error("Error:", error);
            alert(error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleFilterChange = (filterName) => {
        setFilters(prev => ({
            ...prev,
            [filterName]: !prev[filterName]
        }));
    };

    const handleMapLoad = () => {
        setLoading(false);
    };

    const handleMapError = (error) => {
        console.error("Map error:", error);
        setLoading(false);
    };

    const handleStreetViewError = (error) => {
        console.error("Street view error:", error);
    };

    return (
        <>
            <GlobalStyle />
            <Container>
                <HeaderContainer>
                    <HeaderLogo>
                        <svg width="30" height="30" viewBox="0 0 24 24">
                            <path d="M3,13h8V3H3V13z M3,21h8v-6H3V21z M13,21h8V11h-8V21z M13,3v6h8V3H13z" fill="#3e5caa"/>
                        </svg>
                        <Header>Insite BCN</Header>
                    </HeaderLogo>
                    <HeaderNav>
                        <Button>Home</Button>
                        <Button>Menu</Button>
                    </HeaderNav>
                </HeaderContainer>

                <Tagline>Explore Barcelona's Real Estate Market</Tagline>

                <MapView
                    mapUrl={mapUrl}
                    onMapLoad={handleMapLoad}
                    onMapError={handleMapError}
                />

                <PromptContainer>
                    <TextArea
                        value={prompt}
                        onChange={(e) => setPrompt(e.target.value)}
                        placeholder="Enter your query about Barcelona's real estate market..."
                    />
                    <GenerateButton onClick={handlePromptSubmit}>
                        Generate Results
                    </GenerateButton>
                </PromptContainer>

                <FilterContainer>
                    <div>
                        <FilterCheckbox
                            id="metroStation"
                            checked={filters.metroStation}
                            onChange={() => handleFilterChange('metroStation')}
                        />
                        <FilterLabel htmlFor="metroStation">Metro Stations</FilterLabel>
                    </div>
                    <div>
                        <FilterCheckbox
                            id="restaurants"
                            checked={filters.restaurants}
                            onChange={() => handleFilterChange('restaurants')}
                        />
                        <FilterLabel htmlFor="restaurants">Restaurants</FilterLabel>
                    </div>
                    <div>
                        <FilterCheckbox
                            id="bars"
                            checked={filters.bars}
                            onChange={() => handleFilterChange('bars')}
                        />
                        <FilterLabel htmlFor="bars">Bars</FilterLabel>
                    </div>
                    <div>
                        <FilterCheckbox
                            id="parks"
                            checked={filters.parks}
                            onChange={() => handleFilterChange('parks')}
                        />
                        <FilterLabel htmlFor="parks">Parks</FilterLabel>
                    </div>
                </FilterContainer>

                <Analytics
                    statistics={statistics}
                    isLoading={loading}
                />

                <StreetView
                    coordinates={selectedCoordinates}
                    onError={handleStreetViewError}
                />

                <AssetIDcontainer>
                    <AssetIDTextArea
                        placeholder="Enter Asset ID here"
                        value={selectedId}
                        onChange={(e) => setSelectedId(e.target.value)}
                    />
                    <AssetIDConfirmButton onClick={handleConfirmSelection}>
                        Confirm Selection
                    </AssetIDConfirmButton>
                </AssetIDcontainer>
            </Container>
        </>
    );
}

export default App;
