import React, { useState } from 'react';
import {
    Container, Header, HeaderContainer, HeaderLogo, HeaderNav,
    Tagline, MapContainer, StreetView,
    PromptContainer, TextArea, Button, GenerateButton,
    FilterContainer, FilterCheckbox, FilterLabel,
    AnalyticsSection, AnalyticsTitle, BarGraph,
    BuildingInfoSection, BuildingInfoTitle, PropertyDetail,
    PropertyIcon, PropertyValue, GlobalStyle,MapIframe
} from './style.js';

function App() {
  const [prompt, setPrompt] = useState('');
    const [loading, setLoading] = useState(false);
    const [mapHTML, setMapHTML] = useState("http://127.0.0.1:5000/maps/filtered_map.html");
  const [filters, setFilters] = useState({
      metroStation: false,
      restaurants: false,
      bars: false,
      parks: true
  });



  const handlePromptSubmit = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://127.0.0.1:5000/process-prompt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt }),
      });
      const data = await response.json();

      if (data.error) {
        console.error("Error from server:", data.error);
        return;
      }

      // Update the map URL with a timestamp to prevent caching
      const mapUrl = `http://127.0.0.1:5000/maps/filtered_map.html?t=${new Date().getTime()}`;
      setMapHTML(mapUrl);
    } catch (error) {
      console.error("Error fetching filtered map:", error);
    } finally {
      setLoading(false);
    }
  };

    // Mock data for demonstration
    const buildingInfo = {
        price: "€325,000",
        bedrooms: "2",
        bathrooms: "1",
        area: "78m²",
        floor: "3rd"
    };

    const handleFilterChange = async (filterName) => {
        // Update local state
        setFilters(prevFilters => ({
            ...prevFilters,
            [filterName]: !prevFilters[filterName]
        }));

        // Map filter names to layer IDs
        const filterToLayerId = {
            metroStation: 'metro-layer',
            restaurants: 'restaurant-layer',
            bars: 'bar-layer',
            parks: 'park-layer'
        };

        try {
            // Send update to backend
            const response = await fetch("http://127.0.0.1:5000/update-layer-visibility", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    layerId: filterToLayerId[filterName],
                    isVisible: !filters[filterName]  // Use the new value
                })
            });

            const data = await response.json();
            if (data.error) {
                console.error("Error updating layer visibility:", data.error);
            } else {
                console.log("Layer visibility updated successfully:", data.message);
                // Force map refresh by updating the URL with a timestamp
                setMapHTML(prev => `${prev.split('?')[0]}?t=${new Date().getTime()}`);
            }
        } catch (error) {
            console.error("Error sending layer visibility update:", error);
