import React, { useState } from "react";

export default function App() {
  const [mapHTML, setMapHTML] = useState("houses_in_barcelona_map.html");
  const [prompt, setPrompt] = useState("");

  const handlePromptSubmit = async () => {
    const response = await fetch("http://127.0.0.1:5000/process-prompt", {

      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ prompt }),
    });
    const data = await response.json();
    setMapHTML(data.filteredMap); // Server returns the path of the new filtered map.
  };

  return (
    <div className="flex h-screen">
      {/* Left Section - Map */}
      <div className="w-1/2 border-r">
        <iframe
          src={mapHTML}
          className="w-full h-full"
          style={{ border: "none" }}
        ></iframe>
      </div>

      {/* Right Section - Prompt Box */}
      <div className="w-1/2 flex flex-col items-center justify-center p-4">
        <h1 className="text-2xl font-bold mb-4">Interactive Map Filter</h1>
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter a prompt, e.g., 'Show houses under 35k'"
          className="w-full h-20 p-2 border rounded-md"
        ></textarea>
        <button
          onClick={handlePromptSubmit}
          className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md"
        >
          Generate Filtered Map
        </button>
      </div>
    </div>
  );
}
